void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    float t;

    for(int c=0;c<3;c++){
	    vec2 uv = (fragCoord*50.0-iResolution.xy)/iResolution.y/10.0;
        uv += iTime/10.0;
        t = iTime+float(c)/10.;
        float scale = 5.0;
        float scale1 = 1.4;
        for(int i=0;i<20;i++)
        {

            uv=cos(uv/(2.0+sin((uv.x+uv.y)/10.0))-(uv.yx/(2.0+sin(fract(iTime/2.0+uv.x+uv.y))))/scale)*scale/1.5+scale1*scale;
            uv /= scale1;
        	//uv += cos(uv/100.0);
        	uv=uv.yx+col.xy;
        }
     col[c] = fract((uv.x)-(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}
