// random 2D from https://thebookofshaders.com/10/?lan=ch

// Adapted from the code made by Author @patriciogv - 2015
// http://patriciogonzalezvivo.com

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(iMouse)))*10.*iTime);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
 {
    //vec2 st = fragCoord.xy/iResolution.xy;
    //change it into responsive screen 
    vec2 st = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.y, iResolution.x);


    float rnd = random( st );


  if (iMouse.xy==vec2(0)){
      fragColor = vec4(vec3(0.),1.0);


  }else{

    fragColor = vec4(vec3(rnd)*iMouse.x/iMouse.y,1.0);
}
}
