vec4 simu(sampler2D sam, vec2 g, vec2 s, int i, vec4 m, sampler2D kbd )
{
    if (i < 1 || texelFetch(kbd, ivec2(32,1),0).x > 0.)
	{
		float gy = g.y /s.y*2.-1.;
		gy = mod(gy, 0.5) - 0.25;
		gy += (sin(g.x/s.x*6.28318*60.) * 0.5+0.5) * 2./s.y;
		if (abs(gy) < 2./s.y)
		{
			return vec4(0,1,0,1);
		}
		else
		{
			float gx = g.x /s.x*2.-1.;
            gx = mod(gx, 1.0) - 0.5;
			gx += (sin(g.y/s.y*6.28318*30.) * 0.5+0.5) * 20./s.x;
            if (abs(gx) < 2./s.x)
            {
                return vec4(0,1,0,1);
            }
            else
            {
                return vec4(1,0,0,1);
            }
		}
	}
	else
	{
        if (m.z > 0.)
		{
			if (length(g - m.xy) < 50.)
			{
				return vec4(0,1,0,1);
			}
		}

		vec4 c = texture(sam, fract(g/s));

		vec4 l = texture(sam, fract((g+vec2(-1,0))/s));
		vec4 r = texture(sam, fract((g+vec2(1,0))/s));
		vec4 b = texture(sam, fract((g+vec2(0,-1))/s));
		vec4 t = texture(sam, fract((g+vec2(0,1))/s));

		vec4 lp = l+r+b+t - 4.0 * c;

		float feedRate = 0.063;
		float killRate = 0.063;

		float reac = c.r * c.g * c.g;
		float u = 0.22 * lp.r - reac + feedRate * (1.0 - c.r);
        float v = 0.1 * lp.g + reac - (feedRate + killRate) * c.g;
        vec2 sb = c.rg + vec2(u, v);

        return vec4(clamp(sb,0.,1e2),0,1);
	}

    return vec4(0);
}

