#define R iResolution.xy
vec4 T (vec2 U) {return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = T (U)*.99999;
    #define e  vec2 (1,0)
    float dQ = (0.2+0.8*smoothstep(length(U-0.5*R),length(U-0.5*R)-1.,0.4*R.y))*
        ((T(U+e.xy)+T(U-e.xy)+T(U+e.yx)+T(U-e.yx)).x/4.-Q.x);

    /* Z = wavefunction

		Z = dZ*dt/dx + (1+i)*Img(Z) + (0-i)*Real(Z)   (Schr)

		Z = dZ*dt/dx + (1+i)*Img(Z) + (1+0)*Real(Z)   (Wave)


	*/

    //Schr Eq :
      Q.y += dQ-Q.x;
	  Q.x +=    Q.y;
     /*
		Q.y += dQ;
		Q.x += Q.y;
	 */
    //Wave Eq ^






    // Boundaries:
    vec2 M = (U-iMouse.xy)*.08;
    vec2 m = (iMouse.xy-iMouse.zw)*.08;
    if (iMouse.z > 0. ) Q += .5*vec4(vec2(cos(m.x*M.x+m.y*M.y),sin(m.x*M.x+m.y*M.y))*exp(-2.*length(M)),0,0);
    if (iFrame < 1){ vec2 D = (U-0.5*R)*.08;
                    Q.xy = 2.*exp(-.5*length(D))*(vec2(cos(-9.*D.x+D.y),sin(-9.*D.x+D.y)));}
}
