#define CASE 1  // 0, 1, 2 : conditionnal type.
#define TYPE 0  // 0: vec4 1: float

void mainImage( out vec4 o, vec2 U )
{
#if TYPE==0
    vec4 O;
#else
    float O;
#endif

    O -= O;
    for( float i=0.; i<1e4; i++)          // TYPE: 0      1
#if CASE == 0
        O += iTime < i ? i : -i;             // 12 fps    12 fps
#elif CASE == 1
        iTime < i ? O += i : O -= i;         // 8.6 fps   12 fps
#else
        if (iTime < i) O += i; else O -= i;  // 8.6 fps   12 fps
     // if (iTime < i) O += i; else O += -i; // 8.6 fps   12 fps
#endif
    o = vec4(O);
}
