// https://www.youtube.com/watch?v=GznQgTdEdI4

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
uv.x = uv.x*1.7;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 a = vec2(.8,.5);
    float x = a.x-uv.x;
    float y = a.y-uv.y;
    float p = 2.5;
    if(pow(abs(x)/5.,p) + pow(abs(y)/6.,p)<0.001){

        float t = smoothstep(0.9,.0,length(a-uv));
        fragColor = vec4(pow(t,0.16));
    }
    else{
    // Output to screen
    fragColor = vec4(vec3(1.),1.0);
    }
}
