void mainImage(out vec4 O, vec2 C) {
    O=vec4(0);
	float i,g,e,s,l;
    vec3 p,q;
    for(i=0.;++i<50.;){
        vec3 p=g*normalize(vec3((C-.5*iResolution.xy)/iResolution.y,1))
        	+vec3(1,1,iTime);
        q=p;
        s=1.;
        for(int j=0;j++<4;)
            p=mod(p-1.,2.)-1.,
            l=2./dot(p,p),
            p*=l,
            s*=l;
        g+=e=length(p.xy)/s;
        if(e<.003)break;
	}
    O.xyz+=cos(q*.3)*9./i;
}
