float f(float x) {
    return sin(x * 50.) * .2 + .5;
}

vec4 draw(vec2 uv) {
    float diff = f(uv.x) - uv.y;
    if (abs(diff) < .05) {
        return vec4(1.);
    }
    return vec4(0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = draw(uv);
}
