float grid(vec2 p, vec2 res){
    p = mod(p,res.y);
    float d = length(p-res.y*.5);
    return smoothstep(d,d+1.5/iResolution.y, res.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 res = vec2(.1,.2);
    vec2 u = uv;
    u.x +=  mod( u.y, res.x);
    u.x -= u.y+iTime * .1;
    if(uv.y > .5){
   // u.x += sin(iTime)*0.1;
        u +=  mod( u, res.x);
    }
    fragColor = vec4(grid(u,res));
}
