vec2 R;
vec4 t(vec2 u){return texture(iChannel0,u);}

float hash( vec2 p ){ return fract(sin(dot(p, vec2(41, 289)))*45758.5453); }

void mainImage( out vec4 f, in vec2 u )
{
    R = iResolution.xy;
    vec2 uv = u / R;

    vec4 col = t(uv);

    float w = 0.1; 
    vec2 a = vec2(uv.x-0.5, uv.y - 0.66);
    vec2 b = a*0.15/float(10.0);

    uv += b*(hash(uv.xy + fract(iTime))*2.0 );

    for(float i = 1.0; i > 0.0; i-= 0.02)
    {
    	uv -= 0.5;
    	uv*=i;
    	uv += 0.5;

        col += t(uv) * w * 1.5;
        w *= 0.97;
    }
    col *= 0.9;

    f = col;

}

// Dave Hashkins
vec2 hash22(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec2 R;
void mainImage( out vec4 f, in vec2 u )
{
    R = iResolution.xy;
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;

    vec4 col = vec4(1);
    uv.x+=iTime*0.1;
    uv += 12.0;
    vec2 id = floor(uv*8.0);
    vec2 ruv = fract(uv*8.0)-0.5;

    vec2 n =  vec2(pow(sin(iTime*0.4 + hash22(id).x*1.5),2.0),
                  pow(cos(iTime*1.2 + hash22(id*3.0 + 2.0).y*5.0),2.0));

    float d = max(dot(vec2(0.9, -0.8), n), 0.01);
    float c = smoothstep(0.51, 0.3, length(ruv));
    vec3 mcol = mix(vec3(1.0, 0.0, 1.0), vec3(0.0, 1.0, 1.0), d)*c*d;

    col.xyz = mcol;
    f = col;

}

float sq(float t, float f){
    return clamp(cos(f*6.283*t), -1.0, 0.0);
}

vec2 mainSound( in int samp, float time )
{
    vec2 ms = vec2(0);
    float t = mod(time, 101.0); 
    float r = 0.0;
    for(float i = 0.0; i < 1.0; i += 0.01)
    {
        for(float e = 0.0; e < 1.0; e += 0.1)
    	{
    		ms += vec2(sq(t+2.0 + e, 110.0 + i),sq(t+2.0 + e, 221.0 + i)) * 1.3;
        	r++;
        }
    }
    ms /= r;

    return ms * 1.5;
}
