float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 2.0; // scale
    float sp = iTime/2.0;
    uv -= sp;

    vec2 grid = fract(uv);
    float divN = 3.0;
    vec2 id = floor(uv);
    uv = mod(uv, divN);

    float randIdx = rand(id);
    float randIdy = rand(id+vec2(100.0));
    vec2 uv2 = grid;
    float divX = 2.0; float divY = 2.0;
    uv2.x *= (1.0+floor(randIdx*divX));
    uv2.y *= (1.0+floor(randIdy*divY));

    grid = fract(uv2);
    id += floor(uv2)/vec2(divX, divY);

    vec3 col = vec3(rand(id), grid);

    //example
    if(rand(id) < 0.2){
        col = vec3(0.0);
    }

    fragColor = vec4(col,1.0);
}
