vec2 rotateCW(vec2 p, float a)
{
    mat2 m = mat2(cos(a), -sin(a), sin(a), cos(a));
    return p * m;
}

vec3 SoftLight(vec3 a, vec3 b)
{
    vec3 r = (1.0 - a) * a * b + a * (1.0 - (1.0 - a) * (1.0 - b));
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 col = texture( iChannel0, fragCoord/iResolution.xy );
    vec2 _LightPos=vec2(0.5,0.5);

    vec2 distanceToShape = fragCoord.xy/iResolution.xy - _LightPos;

    distanceToShape = rotateCW(distanceToShape, iTime);
	float _Radius=-0.5;
    float _Angle=0.81;
    //shape radius
    float shapeRadius =atan(distanceToShape.x,distanceToShape.y*_Angle);

    float _EdgeSoft=0.1;
	vec3 _LightColor=vec3(0.8,0.4,0.1);
    _LightColor = _LightColor*(1.0-smoothstep(shapeRadius- _EdgeSoft, shapeRadius + _EdgeSoft,length(distanceToShape)));

    //finally changes the color
    vec3 mixCol= SoftLight(col.rgb, _LightColor);

    // Output to screen
    fragColor = vec4(mixCol,1.0);
}
