void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //video that inspired me to do this
    //https://youtu.be/KisZ8PpkXGU



    //vec2 gl_FragCoord = fragCoord.xy;
    vec2 resolution = iResolution.xy;
    float t = iTime;


	vec2 uv = gl_FragCoord.xy / resolution.xy;
	vec2 uv2 = uv;
	vec2 uv3 = uv;
	vec2 uv4 = uv;
	vec2 uv5 = uv;
	vec2 uv6 = uv;
    vec2 uv7 = uv; 
    vec2 uv8 = uv;

    //code for mobile shader complier, don't mind it
	/*if (pointerCount > 0){
		t = time/(time*.25);
	}else{
		t = time;
	}*/

	float s = ((iMouse.x-resolution.x/2.)*.01);

	uv.y += .05*sin(uv.x*20.+(t*s)*2.);
	uv2.y += .05*sin((uv.x-3.1415*.25)*20.+(t*s)*2.);
	uv3.y += .05*(abs(mod(uv.x*25.+(t*s)*1.5,6.)-3.))-.1;
	uv4.y += .05*(abs(mod(uv.x*25.+(t*s)*1.5+3.1415,6.)-3.))-.025;
	uv5.y += .05*sin((1.5*uv.x)*20.+(t*s)*1.15);
	uv6.y += .05*sin((1.5*uv.x-3.1415*.25)*20.+(t*s)*1.15);
	uv7.y += 1.*floor(sin(uv7.x*20.+(t*s)*.25)-.9);
	uv8.y += 1.*floor(sin(uv7.x*15.+(t*s)*.3)-.85);



	//float wb = smoothstep(.0, .005, .1-uv.y);
	//float sb = smoothstep(.0, .005, .15-uv3.y);
	//float wt = smoothstep(.0, .005, uv2.y-.9);

	float sb = smoothstep(uv3.y-.2, 0., .0005);
	float sbb = step(.0, uv3.y-.2);
	float st = smoothstep(uv4.y+.2, 1., .9995);
	float stt = step(uv4.y+.2, 1.);

    //idk why this is like this but it is for some reason
	float wt = smoothstep(uv.y+.1, 1., .998);
	float wtt = step(uv.y+.1, 1.);
	float wb = smoothstep(uv2.y-.1, .0, .002);
	float wbb = step(.0, uv2.y-.1);

	float wt2 = smoothstep(uv5.y+.3, 1., .9995);
	float wtt2 = step(uv5.y+.3, 1.);
	float wb2 = smoothstep(uv6.y-.3, .0, .0005);
	float wbb2 = step(.0, uv6.y-.3);

	float p1 = step(.0, uv7.y);
	float p2 = step(.0, uv8.y);

	float w1 = wb*wbb*wt*wtt;
	float w2 = wb2*wbb2*wt2*wtt2;
	float s1 = sb*sbb*st*stt;



	vec4 col = vec4(1.);
	vec4 col2 = vec4(1.);
	vec4 col3 = vec4(1.);
	vec4 col4 = vec4(1.);
    vec4 col5 = vec4(1.);
    vec4 col6 = vec4(1.);

	//float cl = (sb+(sb-wb));

	col = vec4(1.-w1);
	col2 = vec4(1.-s1);
	col3 = vec4(1.-w2);
	col4 = vec4(1.-0.);
    col5 = vec4(1.-p1);
    col6 = vec4(1.-p2);
	//col *= cl;
	//col2 *= sb;

    //colors are getting manipulated by other cols and it messed up  my head

	col *= vec4(.0, .2, .5, 1.);
	col2 *= vec4(.1, .35, .35, 1.);
	col3 *= vec4(.5, .4, .15, 1.);
	col4 *= vec4(.45, .25, .0, 1.);
	col5 *= vec4(.8, .2, .0, 1.);
	col6 *= vec4(.8, .2, .0, 1.);

	//idk what I did but it works god dammit


	col2 *= col.a-.999;
	col2 *= col.a-2.;
	col5 *= col2-.1;	//???????????????
	col5 *= col2-1.;	//WHAT
	col6 *= col5.a-.999;
	col6 *= col5.a-.2;
	col6 *= col3.a-.999;//WHY
	col6 *= col3.a-2.;	//THIS MAKES NO SENSE TO ME
	col3 *= col2.a-.999;
	col3 *= col2.a-2.;
	col4 *= col3.a-.999;
	col4 *= col3.a-2.;
	col5 *= col3.a-.999;
	col5 *= col3.a-2.;
	col5 *= col4.a-.999;
	col5 *= col4.a-2.;
	col6 *= col5.a-.999;
	col6 *= col5.a-2.;

    fragColor = vec4 (col+col2+col3+col4+col5+col6);

}
