#define bf iChannel0

float D(ivec2 p){
    float c=texelFetch(bf,p,0).w;
    float l=texelFetch(bf,p-ivec2(1,0),0).w;
    float r=texelFetch(bf,p+ivec2(1,0),0).w;
    float u=texelFetch(bf,p+ivec2(0,1),0).w;
    float d=texelFetch(bf,p-ivec2(0,1),0).w;
    return l+r+u+d-4.*c;
}

void mainImage( out vec4 col, in vec2 p ){
    float k;
    if (iFrame==0){
    	vec2 q=10.*(p-0.5*iResolution.xy)/length(iResolution.xy);
        float x=q.x+0.5,y=q.y+0.7;
        k = x*x*x*(x-2.)+y*y*y*(y-2.)+x<0.?1.:0.;
    }
    else{
        k=texelFetch(bf,ivec2(p),0).w;
    	if (iMouse.z>0.&&length(p-iMouse.xy)<=10.) k+=.01;
    	k+=0.2*D(ivec2(p));
    }
    col = vec4(vec3(0.0),k);
}
