const float M_PI = 3.14159265358979323846264338327950288;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 coords = fragCoord / iResolution.xy;
    vec2 ratio = vec2(iResolution.x / iResolution.y, 1.0);

    vec2 circleCenter = vec2(0.5, 0.5);
    float circleRadius = 0.4;

    float red = coords.x < 0.5 ? (cos(coords.x * 2.0 * M_PI) + 1.0) / 2.0 : 0.0;
    float green = (cos(coords.x * 2.0 * M_PI - M_PI) + 1.0) / 2.0;
    float blue = coords.x < 0.5 ? 0.0 : (cos(coords.x * 2.0 * M_PI) + 1.0) / 2.0;
    vec4 color = vec4(red, green, blue, 1.0);
    vec4 noColor = vec4(0.0, 0.0, 0.0, 1.0);

    bool inCircle = length((coords - circleCenter) * ratio) < circleRadius;
    bool inBand = mod(coords.x * ratio.x + coords.y, 1.0 / 20.0) < 1.0 / 50.0;

    if (inCircle || inBand) {
        fragColor = noColor;
    } else {
        fragColor = color;
    }
}
