void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = 8.*(2.*iMouse.xy-iResolution.xy)/iResolution.y;
    float pi = 3.14159265359;

    vec2 p = cos(pi*11./6.+vec2(0.,pi*.5))*(4.+sin(iTime));
    if(iMouse.z > .5) p = m;

    u = u*length(p);

    for(float i = .5; i < 20.; ++i)
    {
        float e = mod(i,2.)-.5;
        e = mix(   3.,												//close hand fan
                1./3.,												//open  hand fan
                e);										
        u = sin(atan(u.x,u.y)*e+vec2(0.,pi*.5))*pow(length(u),e);	//space wrap
        u-= p;														//move space	
    }
    fragColor = vec4(.2,.2,.3,.0)*length(p)/length(u);
}
