vec2 fmod(float i, float m)
{
    return vec2(mod(i,m)*(1.0/m), abs(i/m));
}
vec4 c(vec2 uv)
{
    //uv.x += cos(uv.y*50.0+iTime)*0.01;
    // Plane tilting
    uv.x *= sin(iTime);
    uv.y *= cos(iTime);
    float r = iTime;
    // Rotating around Z axis
    vec2 uvR = mat2(cos(r),-sin(r),sin(r),cos(r))*uv;
    // Angle around Z axis

    float angl = atan(uvR.y,uvR.x);

    // Size of white stripe spread
    float spread = tan(iTime)*sin(5.0*angl);

    float l = length(uvR);
    vec2 a = fmod(l+spread*angl*0.9,0.1);
    float stripe = (a.x*(0.3/a.y));

    return vec4(1.0,0.0,0.0,0.0)*stripe+a.y*0.005*tan(iTime*5.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(col,1.0);
    vec4 cl = c(uv);
    //cl *= 1.0/(5.0*length(uv)+1.0);
    fragColor = cl;
}
