void mainImage( out vec4 O, vec2 u )
{
    vec2 U = u/R;
    O = step(U.y , T(vec2(U.x*640.,0)) / float(iFrame) );
    if ( int(u.x) == 333 ) O.b++;
}

void mainImage( out vec4 O, vec2 U )
{
    O = T(U);
    float t = T(vec2(0)).w;
    if ( int(U.x) == int(20.*iTimeDelta/1e-3) ) O.r ++;
    if ( int(U.x) == int(20.*(iTime- t)/1e-3) ) O.g ++;
    if ( U==vec2(.5) ) O.w = iTime;
}
