// inspired and golfed from https://shadertoy.com/view/wtcSDX

void mainImage(out vec4 O, vec2 u) {
    vec2 R  = iResolution.xy, U  = u+u - R ;
    O += abs( cos( 3.* atan(U.y,U.x) -iTime )) - length(U)/R.y *vec4(1,.8,.6,0);
} 
