void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1, 0, 0, 1) * texelFetch(iChannel0, ivec2(fragCoord), 0);

    vec2 xy = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec4 hightLight = vec4(1);
    drawHeart(hightLight, xy, 0.03, float(iFrame));

    if (hightLight.a > 0.)
        fragColor = hightLight;
}

#define M_PI 3.14159265358

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 xy = (2. * fragCoord - iResolution.xy) / iResolution.y;
    drawHeart(fragColor, xy, 0.02, float(iFrame));
    if (fragColor.a < 1.)
        discard;
}

#define M_PI 3.14159265358

void drawHeart(out vec4 fragColor, in vec2 xy, in float pointSize, float fFrame) {
/*
    t = 0 → 2𝜋
    x = 16 sin³(t)
    y = 13 cos(t) - 5 cos(2 t) - 2 cos(3 t) - cos(4 t)
*/
    float t = fFrame * M_PI / 360.;
    float x = 16. * sin(t) * sin(t) * sin(t);
    float y = 13. * cos(t) - 5. * cos(2. * t) - 2. * cos(3. * t) - cos(4. * t);

    vec2 hpos = vec2(x, y);
    hpos *= .055;
    hpos.y += .2;

    vec2 xyS = vec2(-xy.x, xy.y);

    if (distance(hpos, xy) < pointSize || distance(hpos, xyS) < pointSize)
        fragColor = vec4(1);
    else
        fragColor = vec4(0);
}
