   vec2 linear(vec2 uv)
   {
        return uv; 
   }

   vec2 circle(vec2 uv)
   {
        vec2 nuv = normalize(uv);
        float dist = 1.0/length(uv);
        return nuv*dist;
   }
   vec2 polar(vec2 uv)
   {
        return vec2(atan(uv.x,uv.y),sqrt(uv.x*uv.x+ uv.y*uv.y));  
   }
   vec2 spherical(vec2 uv)
   {
        vec2 direction = uv;
        float len = length(uv);
        return vec2(exp(len))*direction;
   }
   vec2 fisheye(vec2 uv)
   {
        float f = 2.3000;
        // Calculate angle from plane's UV
        vec2 angles = atan(uv, vec2(1.0));
        //return angles*f;
        return vec2(2.0)*sin(angles*vec2(0.5))*vec2(f);
   }

   float inv(float x)
   {
    return 1.0/x;
   }



   void mainImage(out vec4 fragColor, in vec2 fragCoord) {
       vec2 uv = fragCoord/iResolution.xy;
       uv *= 2.0;
       uv += -1.0;
       float t = iTime*1.0000;
       vec2 resultingUv = circle(vec2(uv.x, uv.y)*vec2(0.9000));
       float x = resultingUv.x;
       float y = resultingUv.y;
       float program = cos(mod(t+mod(mod(sin(y),0.69),1.67)+cos(1.41*x*cos(t*cos(t*x)))*mod(15.22,1.30)+x*cos(16.80)+mod(sin(sin(x*2.62)),8.08),9.38));
       float parameter = clamp(program, 0.0,1.0);

       vec3 colorStart = vec3(0,0,0)/255.0;
       vec3 colorEnd = vec3(255,51,0)/255.0;
       vec3 resultColor = mix(colorStart, colorEnd, parameter);

       bool hasColorThreshold = false;
       if(hasColorThreshold)
       {
          resultColor = (parameter > 0.5000)?colorStart:colorEnd;  
       }
       fragColor = vec4(resultColor, 1.0);
   }
