float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy) / iResolution.y;
    vec3 buff = texture(iChannel0, 0.985*(fragCoord/iResolution.xy - 0.5) + 0.5).rgb;
    vec3 col = vec3(0);

    float a = fract(iTime)*3.14*2.;
    float co = cos(a), si = sin(a);
    uv *= mat2(co, -si, si, co);

    float t = cos(a)*.5+.5;
    float d = sdBox(uv, vec2(0.125));

    col = hsv2rgb(vec3(iTime, 1.0, 1.0)) * 0.000001/(d*d);

    fragColor = vec4(mix(col, buff, 0.9), 1.0);
}
