void mainImage( out vec4 O, vec2 U )
{
    float y =  U.y/R.y;
    O = U.x > 1.
          ? T(U-vec2(1,0)) 
          : vec4( 4.*(2.*y-1.) < iFrameRate - 1./iTimeDelta  );

    if (mod(iTime,10.) < 5. ) { // every 5 seconds, 5 secondes of time varing 
        if (U.x<1.) O.rg *= .9;
        for( float i=0.; i < (.5+.5*sin(.3*6.28*iTime))*1e4; i++) O.a++;
    }

    if (U.x<1.) O = mix(O, vec4(1,0,0,0), max(0.,1.-abs( y -    iFrameRate/70. )/2.*R.y) ),
                O = mix(O, vec4(0,1,0,0), max(0.,1.-abs( y - 1./iTimeDelta/70. )/2.*R.y) );

}
