float noise1d(float n){
    return fract(cos(n*89.42)*343.42);
}
float noise2d(vec2 co){
    return fract(sin(dot(co.xy ,vec2(1.0,73))) * 43758.5453);
}

vec2 hash2( vec2 p )
{
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 p )
{
//29 seconds, 29 fps
    vec2 uv = p.xy / iResolution.xy; 
    p = uv;
    float w = length(p);

    float s = p.x + pow(sin(iTime*.1),4.)*(.2*atan(p.y-.5,p.x-.5)+iTime*.05);
    vec2 samp = vec2(s, p.y);

    vec4 col = texture(iChannel0, samp);

	fragColor = vec4(col);
}
