const float maxi = 20.;
float Circle(vec2 uv, vec2 p, float radius, float blur){

    float d = length(uv - p);    
    float c = smoothstep(blur,0.,abs(d-radius));

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord -.5 * iResolution.xy) / iResolution.y;
    float blur = 1.5 / iResolution.y;
    float radius =  0.0625;
    float col = 0.;

    for (float i=0.; i<maxi; i++){

        vec2 p = vec2(sin(i+iTime), cos(i)) * .25;
		//radius *= (cos(iTime) *.5 +.5);
		radius *=  1.+1. / maxi;
        col += Circle(uv, p, radius, blur);
        col *= 1. -(1. / maxi);
    }
	        fragColor = vec4(vec3(col),1.0);

}
