void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

   uv -= vec2(0.5,0.5); //we make the whole thing black
   uv *= -0.05; //we remap the coordinates to become between -1 and 1
 float distance = length(uv);
   float angle = atan(uv.y,uv.x);

   float color = sin(angle+distance*1000.0)*cos(angle+distance*iTime - iTime)*100.0;

   fragColor = vec4(color, color, color, color); 
}

