mat2 r(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}

float bump(vec2 uv,float offset) {
 uv *=r(iTime+offset);
 return smoothstep(0.,4.,abs(cos(iTime+2.*atan(uv.x,uv.y))))/8.;
}
float circl(vec2 uv,float r,float offset) {
    return smoothstep(
    0.09-(length(uv)*.05),
    0.1,
    length(uv)-r- 
    bump(uv,offset)
    );
}
float fig(vec2 uv, float offset) {

return max(circl(uv,.33,offset) , 1.-circl(uv,.3,offset));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy )/ iResolution.y;
      vec2 uuv = uv;  
      uv*=2.;

      vec2 gid = floor(uv);
      uv *= r(atan(uv.x,uv.y));
      uv *=r(iTime*.123);

      uv = fract(+iTime*.124+uv*length(0.1*cos(uv+iTime)*10.))-.5;

    float d = min(fig(uv,0.) ,fig(uv,3.15/4.)) ;

    vec3 col = vec3(0.);
    col.r = fig(uv,0.+sin(-iTime+gid.x*11.));
    col.g = fig(uv,0.3-cos(iTime-gid.y*47.));
    col.b = fig(uv,-0.3+tan(sin(length(gid)+iTime)));
    fragColor = vec4(col,1.0);
}
