// gigatron France (c)
vec3 cpow(vec3 color) {


     return abs(vec3(color.rrr*pow(color.r,0.1)))*
            abs(vec3(color.ggg*pow(color.r,0.2)))*
         	abs(vec3(color.bbb*pow(color.r,0.3)));
         	//abs(vec3(color.rgb/pow(color.r,0.0))) /*limit */
                                                 ;
 }


float cercle(vec2 uv, vec2 centre , float r) {


    uv.x *= iResolution.x/iResolution.y;

    return 1.0-smoothstep(r-0.2,r+0.2,distance(uv,centre));

}    



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec3 col  = vec3(0.8,0.4,0.2) ;
       	 col +=  sin(iTime)*0.5+0.5*cercle(uv,vec2(0.5+sin(iTime)*0.5+0.5,0.5),0.5);

    vec3 tx  = cpow(texture(iChannel0,uv).xyz);
         tx  = tx*col;

    vec3 tx2 = cpow(texture(iChannel0,uv*1.01).xyz)*3.0;

    vec3 final = vec3(max(vec3(tx.r,tx2.g,tx2.b),tx/tx2))+mix(tx2,tx,col); // you can have a million 
                                                                // fx ! with a million/ pixel




    // if (t>0.1) discard;

    // if( tx.r>=0.999 ) discard; /// for future usage ; 


    fragColor = vec4(final  ,1.0);




}
