void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float w = iResolution.x / 16.;
    float h = iResolution.y / 15.;

    float j = mod(fragCoord.y / h, 4.);

    float xPos = fragCoord.x;
    if (j > 3.)
        xPos -= 10.;
    else if (j > 2.)
        xPos -= 20.;
    else if (j > 1.)
        xPos -= 10.;

    float i = mod(floor(xPos / w), 2.);

    fragColor = vec4(1);
    if (i >= 1.0) {
        fragColor = vec4(0);
    }

    float ij = mod(fragCoord.y, h);
    if (ij < 1.)
        if (i >= 1.)
        	fragColor = vec4(1);
        else
            fragColor = vec4(0);
}
