vec3 CrossStiching(in vec3 inColor, in vec2 uv)
{
    float timer = sin(iTime * 2.f) * 0.02f;
    float l1 = 1.0f + timer;
    float l2 = 0.7f + timer;
    float l3 = 0.4f + timer;
    float l4 = 0.2f + timer;
    float color = 1.f;
    float value = length(inColor);// * 0.577f;
    if ((value < l1 && mod(uv.x + uv.y, 10.f) >= 0.f) && (value < l1 && mod(uv.x + uv.y, 10.f) < 1.f))
        color = 0.f;
    if ((value < l2 && mod(uv.x - uv.y, 10.f) >= 0.f) && (value < l2 && mod(uv.x - uv.y, 10.f) < 1.5f))
        color = 0.f;
    if ((value < l3 && mod(uv.x + uv.y - 5.f, 10.f) >= 0.f) && (value < l3 && mod(uv.x + uv.y - 6.f, 10.f) < 2.f))
        color = 0.f;
    if ((value < l4 && mod(uv.x - uv.y - 5.f, 10.f) >= 0.f) && (value < l4 && mod(uv.x - uv.y - 6.f, 10.f) < 2.5f))
        color = 0.f;

    return vec3(color);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0, uv);

    // Output to screen
    fragColor = vec4(CrossStiching(fragColor.rgb, fragCoord), 1.0);
}
