#define MAX_STEPS 64
#define EPS vec2(1e-5,0.0)
#define PI 3.1415926


vec3 rotate(vec3 p,float angle,vec3 axis){
	vec3 a=normalize(axis);
	float s=sin(angle);
	float c=cos(angle);
	float r=1.0-c;
	mat3 m=mat3(
		a.x*a.x*r+c,
		a.y*a.x*r+a.z*s,
		a.z*a.x*r-a.y*s,
		a.x*a.y*r-a.z*s,
		a.y*a.y*r+c,
		a.z*a.y*r+a.x*s,
		a.x*a.z*r+a.y*s,
		a.y*a.z*r-a.x*s,
		a.z*a.z*r+c
		);
	return m*p;
}

vec2 pmod(vec2 p,float n){
	float np=3.141592*2.0/n;
	float r=atan(p.x,p.y)-0.5*np;
	r=mod(r,np)-0.5*np;
	return length(p)*vec2(cos(r),sin(r));
}

float sphere(vec3 p,float r){
	return length(p)-r;
}

float plane(vec3 p){
	return dot(p,vec3(0.2,3.0,0.0)+0.5);
}

float distFunc(vec3 p){
	vec3 q=rotate(p,radians(PI)*iTime*10.0,vec3(0.0,0.0,1.0));

	q.xy=pmod(q.xy,6.0);


	float s1=sphere(q+vec3(0.0,0.0,0.0),max(1.0,sin(iTime)*2.0));
	float s2=sphere(q-vec3(0.0,0.0,0.0),max(1.0,sin(iTime)*1.9));
	float p1=plane(q);
	return max(max(s1,-s2),p1);
}

vec3 sceneNormal(vec3 p){
	float d=distFunc(p);
	return normalize(vec3(distFunc(p+EPS.xyy)-d,distFunc(p+EPS.yxy)-d,distFunc(p+EPS.yyx)-d));
}


void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 p=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

	vec3 ro=vec3(0.0,0.0,2.0);
	vec3 rd=normalize(vec3(p,-1.0));
	float d,t=0.0;
	vec3 n,rp;

	for(int i=0;i<MAX_STEPS;i++){
		rp=ro+rd*t;
		d=distFunc(rp);
		if(abs(d)<1e-5)break;
		t+=d;
		}

		n=sceneNormal(rp);	

	fragColor=vec4(n,1.0);

}
