#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(t) (cos((vec3(0,2,-2)/3.+t)*6.24)*.5+.5)
void mainImage( out vec4 O, vec2 C)
{
    for(float g,d,e,i=0.;
        ++i<99.;
        g+=e*.5
        )
    {
        vec3 r=iResolution,
        p=R(vec3(g*(C-.5*r.xy)/r.y,g+iTime*1.),
            vec3(.577),
            iTime*.02
            );
        p.xz=mod(p.xz,6.)-3.;
        e=max(
            .02,
            abs(length(p.xz)-2.)+
            (
                (abs(
                    dot(
                        vec2(atan(p.x,p.z)/3.14,mod(p.y+iTime,4.)-2.),
                        vec2(.7)
                    )
                )>.7
            )?.2:0.));
        O.xyz+=mix(vec3(1),H(g*.1),.8)*exp(-e*9.)*.03;
    }
}
