float draw_char(vec2 coord, ivec2 char) {
    int pos_id = int(coord.x * 7.0) + int(coord.y * 9.0) * 7;
    int num = pos_id < 32 ? char.x : char.y;

    int val = (1 << pos_id) & num;
    float col = val == 0 ? 0.0 : 1.0;

    return col;
}

float draw_text(vec2 coord, ivec2[64] text_string, ivec2 text_box) {

    int i = 0;
    float out_col = 0.0;

    coord.x *= float(text_box.x)-.5;
    coord.y *= float(text_box.y)-.5;
    int char = int(coord.x) + (text_box.y - int(coord.y) - 1) * text_box.x;
    coord.x -= float(int(coord.x));
    coord.y -= float(int(coord.y));
	return draw_char(coord, text_string[char]);
}


float Hash21(vec2 p) {
    p = fract(p*vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}

mat2 Rot(float a){
    float s=sin(a);
    float c=cos(a);
    return mat2(c,-s,s,c);
}

float box(vec2 uv,vec2 size,vec2 position,float blur){
 uv.x=abs(uv.x);
 float base=ss(-blur,blur,uv.y-position.x);
 base*=ss(blur,-blur,uv.y-position.y);
 float b2=mix(size.x,size.y,(uv.y-position.x)/(position.y-position.x));
 float b3=ss(blur,-blur,uv.x-b2);
// float b3=ss(blur,-blur,uv.x-.2);
 return base*b3;
 }

float star(vec2 uv,float blur,float rot){

 uv=uv-.5;
 uv*=Rot(rot*iTime*.8);
 uv*=2.+rot;
 vec3 col=vec3(0.);

 float l=length(uv);
 float ll=.29/l;
 col=ll*vec3(ss(.9,.2,l));


 vec2 size=vec2(0.,.1);
 vec2 pos=vec2(-.4,-.2);
 float tapered=box(uv,size,pos,blur);

 size=vec2(.4,.2);
 pos=vec2(-.2,.0);
 tapered+=box(uv,size,pos,blur);


 size=vec2(.2,.4);
 pos=vec2(.0,.2);
 tapered+=box(uv,size,pos,blur);


 size=vec2(.1,.0);
 pos=vec2(.2,.4);
 tapered+=box(uv,size,pos,blur);


 col+=tapered;
 return col.x;
}

vec3 starLayer(vec2 uv,float scaleFactor){
    vec3 col=vec3(0);
    float flare=0.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);
    for(int y=-1;y<=1;y++){
    for(int x=-1;x<=1;x++){
    vec2 offs=vec2(x,y);
    vec2 rand=vec2(sin(Hash21(id-offs)*iTime)*.2,cos(Hash21(id-offs)*iTime)*.2);

    flare=star(gv-rand+offs,0.1,rand.x);

    float h=Hash21(id-offs);
    vec3 color=vec3(flare+h,flare+fract(h*35.23),flare+fract(h*632.33));
    col+=flare*color*.5;
    }
    }
    col=clamp(col,0.,1.);
    float dist=ss(0.,8.,scaleFactor);
    col-=vec3(dist*.9,dist*.7,dist*.5);    
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvTxt = fragCoord/iResolution.xy;

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col=vec3(0);
    vec2 uv2=vec2(0);
    float time=iTime*.1;
    float scaleFactor=0.;

    for(float i=0.12;i<1.;i+=0.088){ 
    scaleFactor=fract(i-time)*25.;
    uv2=uv*scaleFactor;
    col=max(starLayer(uv2+i*423.225,scaleFactor),col);
                                }
//    col+= vec3(draw_text(uvTxt, text_string, text_box_chars));
    if(draw_text(uvTxt, text_string, text_box_chars)==1.)
    {
    col=1.-col;
    }
    fragColor = vec4(col,1.0);
}
