float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

mat2 Rot(float a) {
	float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Square(vec2 uv, float size) {
	vec2 s = abs(uv) - vec2(size);
    float square = length(max(s, .0));
    square = smoothstep(.001, .0, square);
    return square;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t = iTime;
    //uv *= Rot(3.141592*.25);
    float tresh = .14;
    //if (uv.y < tresh && uv.y > -tresh && uv.x < tresh && uv.x > -tresh) uv *= Rot(iTime);
    //if (length(uv) < tresh && length(uv) > -tresh) uv *= Rot(iTime);

    uv *= 3.9;
    //uv.x *= .6;
    uv.x += .6;
    uv.y += .6;
    float a = 1.2;//fract(iTime*.02);
    uv *= a-uv;

    vec3 col = vec3(0.);

    float d = sdCircle(uv,0.5+fract(t*.1)*.5-.25);
    vec3 circle = vec3(1.0) - sign(d)*vec3(0.0,0.0,0.0);
    circle = circle * vec3(smoothstep(0.6, .0, length(uv)));

    col = circle;
    col *= 0.1 + 1.*cos(100.0*d);

    d = sdCircle(vec2(uv.x+-.00, uv.y),fract(t*.19)*0.94);
    circle = vec3(1.0) - sign(d)*vec3(0.0,0.0,0.0);
    col += circle;
    col *= 0.9 + 1.*cos(20.0*d);

    //col += circle * vec3(1.0, 0., 0.);
    //col -= circle * vec3(.0, .7, .5);

    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv *= uv/uv.x+.0;

    float square = Square(uv, 0.25);
    col *= square;
    col *= smoothstep(.34, .3, length(uv));
    col = clamp(col, 0., 1.);
    col -= vec3(.9, .8, .9);

    col += vec3(0.,0., 1.) * smoothstep(0.8, 0.0, length(uv));

    col += vec3(1.,1., 1.) * smoothstep(0.3, 0.0, length(uv));

    fragColor = vec4(col,1.0);
}
