float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
 //  uv.y-=iTime*0.2;   
    float s = 4.0;
    vec2 u = fract(uv*s);
	float k = step(u.x,0.5);
   float r = rand(floor(uv*s)); 
    float t = iTime;
    t-=sin(t*r)+1.0;
   float f = (sin(t*r*10.)+1.)*.25;
    float w = r > .5 ? u.y : u.x;
    k = step(f,w) - step(f+.25,w-.25);

    vec2 d = fract(u);
    k+= (step(d.x,.005) + step(d.y,.005))*.3;   
    fragColor = vec4(k);
}
