#define CS(a) vec2(cos(a),sin(a))


float circle(vec2 p,float scale)
{
	float f = 0.0;
    p.x+=sin(p.y*20.0+iTime*10.)*0.05;
    f += 0.04/abs(length(p)- 0.1*scale);
	return f;
}

vec3 rainbowCircle(vec2 p)
{
    vec3 colA = vec3(0.2, 0.7, 1.0);
    vec3 colB = vec3(0.5, 0.1, 0.3);
    vec3 colC = vec3(0.1, 0.4, 0.6);

    float dz= texture( iChannel0, vec2( .01, 0.55 ) ).x;
   	p*=dz;
    float d=circle(p,4.); 

    float da= texture( iChannel0, vec2( .01, 0.55 ) ).x;
    p*=da;
    float h=circle(p,4.);

    float du= texture( iChannel0, vec2( .0, 0.0 ) ).x;
    p*=da;
    float o=circle(p,4.);

    vec3 ss=max(max(d*colA,h*colB),o*colC);
    return ss;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p=(fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 cir=rainbowCircle(p);   
   // cir=exp(-cir);
    fragColor = vec4(cir,1.);
}
