mat2 r2(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(s,c,-c,s);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord/iResolution.xy) -1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 1.0 + dot(uv,uv)*.13;

    uv*=2.5+sin(iTime/2.)*.5;
    uv.x += sin(iTime)*6.;
    uv.y += cos(iTime)*5.5;
    uv *= r2(sin(.75*iTime+cos(iTime*.5)));

    vec2 id = floor(uv);

    uv.x += fract(sin(id.y*845.45)*8788.88)*iTime*4.;
    id = floor(uv);
    uv=fract(uv)-.5;
    float rnd  = fract(sin(id.x+id.y * 761.33)*7645.45);
    float rnd2 = fract(sin(id.x+id.y  * 56.45)*578.67);

    float rnd3 = fract(sin(id.x+id.y  * 556.45)*5878.67);
    float rnd4 = fract(sin(id.x+id.y  * 756.45)*7778.67);
    float rnd5 = fract(sin(id.x+id.y  * 856.45)*8878.67);

    rnd = rnd < .49 ? 1.0: -1.0;
    float grd = step(.475, uv.x) + step(.475, uv.y);
    uv*=r2(rnd2+rnd*iTime);
    vec4 tx = (rnd < .5) ? texture( iChannel0, sin(rnd2*iTime) *    uv-.5) :
    					   texture( iChannel1, sin(rnd2*iTime*1.25)*uv-.5);
    tx = (rnd2>.25) ? tx : texture( iChannel2, sin(rnd2*iTime*1.5)* uv-.5);
    tx = (rnd2<.75) ? tx : texture( iChannel3, sin(rnd2*iTime*1.3)* uv-.5);
    float bmp = sin(uv.x*16.-iTime*3.) * sin(uv.y*19.+iTime*2.75);            //
    vec3 col = tx.rgb*vec3(max(rnd3,.7),max(rnd4,.8),max(rnd5,.75)); 

    fragColor = vec4(col,1.0); // * grd;
    fragColor += grd;
}

