#define tau 6.28318530718

float coss(float x){
	return (1. + cos(x * tau))/2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv =  ( fragCoord -.5*iResolution.xy ) / iResolution.y;


    uv *= 20.;

    float t = iTime;
    vec2 pos = vec2(cos(t) * 5., sin(t) * 5.);
    float d = length(uv - pos) * 0.5;

    uv = pos + (uv-pos) * (1. + 1. / (0.1 + d));


    pos = vec2(cos(t + tau / 2.) * 5., sin(t + tau / 2.) * 5.);
    d = length(uv - pos) * 0.1;

    uv = pos + (uv-pos) * (1. + 1. / (0.1 + d));


    // Output to screen
    float val = pow(coss(uv.x) * coss(uv.y), 0.2);
    vec3 col = vec3(val, val, val);


    fragColor = vec4(col,1.0);
}
