float lerp(float t0, float t1, float y0, float y1, float t) {
    return y0 + (y1 - y0) * (t - t0) / (t1 - t0);
}
vec3 lerp(float t0, float t1, vec3 y0, vec3 y1, float t) {
    return y0 + (y1 - y0) * (t - t0) / (t1 - t0);
}

float lerp2(float y0, float y1, float t) {
    return y0 * (1.0 - t) + y1 * t;
}
vec3 lerp2(vec3 y0, vec3 y1, float t) {
    return y0 * (1.0 - t) + y1 * t;
}

vec3 gradient(float f, float t) {
    vec3 color = f + vec3(0.0, 0.3 + sin(3. * t) * 0.2, 0.7 + cos(4. * t) * -0.2);
    color = cos(color * 3.14159 / 2.);
    color = 1. - color * color;
    return color;
}

vec3 bands(float f, float t) {
    if (fract(f) < 0.1) {
        return vec3(0., 0., 0.);
    } else {
        return vec3(1., 1., 1.);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float t = iTime;
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y * 3. / lerp(-1., 1., 1., 3., sin(t*0.2))
        + vec2(t*0.1 + cos(t*0.4),
               t*0.2 + sin(t*0.3));
    vec3 col = gradient(
        1.2 * cos(  8.4 * uv.x -  1.1 * uv.y + 0.8 * t + 1.) +
        0.8 * cos(  1.5 * uv.x +  4.2 * uv.y - 0.1 * t + 5.) +
        1.0 * cos( -4.6 * uv.x +  1.1 * uv.y - 0.4 * t + 6.) +
        1.0 * cos( -1.2 * uv.x -  6.2 * uv.y + 0.6 * t + 9.) +
        0.5 * cos( 14.1 * uv.x +  1.9 * uv.y + 3.2 * t + 1.) +
        0.5 * cos( 12.1 * uv.x - 11.8 * uv.y - 2.8 * t + 7.) +
        0.3 * cos( 27.2 * uv.x + 14.0 * uv.y + 0.1 * t + 0.) +
        0.2 * cos( 14.2 * uv.x + 27.0 * uv.y + 0.3 * t + 0.),
        t
    );
    fragColor = vec4(sqrt(col),1.0);
}
