#define PI  3.141592
#define TAU 6.283184


vec2 cs(float a){
    return vec2(cos(a),sin(a));
}

vec2 rot(vec2 v, float a) {
    vec2 c = cs(a);
    return v * mat2x2(c.x, -c.y, c.y, c.x);
}

vec2 sp(float t){
    return vec2(floor(t), fract(t));
}

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
float srand(vec2 co){ return 2.*rand(co)-1.; }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
	fragColor.b=0.;
    for(float i = 0. ; i < 40. ; i++) {
	    vec2 t = sp(iTime*.25 - i*.1);
	    vec4 c = vec4(
	        srand(vec2(t.x, i+0.)),
	        srand(vec2(t.x, i+1.)),
	        rand(vec2(t.x, i+2.))*1.0+0.5,
            srand(vec2(t.x, i+3.))*.2
	    );


	    uv -= c.xy;
        if(length(uv)<c.z) {
            // 0 -> 1 -> 0 smoothly.
            float twistPercent = .5-cos(t.y*TAU )*.5;

            // 0 on outside, 1 at center
            float twistAmp = (c.z-length(uv))/c.z;
            //twistAmp *= twistAmp;
            // max twist (radians)
            float peak = PI*c.w;

            float angle = twistAmp * twistPercent * peak ;
            uv = rot(uv,angle);

        } else if(length(uv)<c.z+.01) {
            fragColor.b+=sin(t.y*PI);
        }
        uv += c.xy;
    }

    fragColor = max(fragColor, vec4(fragColor.b));
    fragColor *= texture(iChannel0, uv);

    return;
}
