void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y - 0.5;
    uv.x -= 0.4;

    float col1 = 0.0+0.9*sqrt(1.0 - (sin(1.0+0.8*iTime)*3.0+6.5)*(uv.x*uv.x + uv.y*uv.y)); // sphere
    float col2 = 2.0-1.0*sqrt(1.0 + (sin(3.0*iTime)+3.0)*(uv.x*uv.x + uv.y*uv.y)); // hyp2
    float col3 = 0.0+0.3*sqrt(-1.0 + (4.0*cos(iTime)+16.0)*(uv.x*uv.x + uv.y*uv.y)); // hyp1

    // Output to screen
    fragColor = vec4((0.5+0.5*sin(iTime))*col3,(0.5+0.5*cos(iTime))*col2,col1,1.0);
}

