float N = 2048.;
vec2 mouse = vec2(0.);

float mandelbrot(vec2 c)
{
    vec2 z = vec2(-.3, .0);
    for(float i = 0.; i < N; i++)
    {        
        if(length(z)>2.)
            return i;
     	else 
        	z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y)+c;
    }
    return N;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ((fragCoord/iResolution.xy)-vec2(.5))*5.;
    // Time varying pixel color
    vec3 col = vec3(0);
	mouse+=vec2(-1.37, -.01);
    float value = mandelbrot((uv.xy/(iTime*(iTime*iTime)+1.)+mouse))/N;
	col = vec3( sin(fract(value*value*20.)), sin(fract(value*7.)*5.),  sin(value*value*5.));
    // Output to screen
    fragColor = vec4(col,1.0);
}
