void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 polar = vec2(atan(uv.x,uv.y)/6.28,length(uv));

    float rotationSlowness = .008;
    uv = vec2(polar.x-iTime*rotationSlowness, polar.y);

    //float c = fract(sin(uv.x*200. + uv.y*53.)*5279.);
    //float c = fract(sin(uv.x*123.));
    float c = fract(fract(sin(uv.y*123.)) + fract(cos(uv.x*123.)));    

    //c *= fract(sin(uv.y*10.));
    //c *= fract(sin(uv.x*12.));

    float r = c;
	float g = smoothstep(.1, .3, c);
    float b = smoothstep(.2, .4, c);

    vec3 col = vec3(r, g, b);

    fragColor = vec4(col,1.0);
}
