void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 f = vec3(1., 1., 1.); //factor
    vec3 o = vec3(.5, .75, 1); //offset
    o -= (o.r + o.g + o.b)/3.; //center (subtract average)

    vec3 col = 0.5 + 0.5*-cos( 6.283*(uv.x + o + iTime*.125) );

    col = pow(col, f);
    //col *= vec3(1., .75, .5);
    //col = .01/abs(col-uv.yyy); //graph

    fragColor = vec4(col, 1);
}
