void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 polar = vec2(atan(uv.x,uv.y)/6.28,length(uv));

    // change angle depending on the radius to produce wrapping result
    polar.x += polar.y;

    float rotationSlowness = .2;

    uv = vec2(polar.x-iTime*rotationSlowness, polar.y);
    //uv = vec2(polar.x, polar.y);

    float dials = 10.;

   	float x = uv.x*dials;
    float m = min(fract(x), 1.-fract(x));
    float c = smoothstep(1., 0., m);

    fragColor = vec4(c);
}
