void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0,fragCoord / iResolution.xy);
}

#define runFor 300.0
//change define to run for N frames before restarting

vec4 read(vec2 fragCoord, vec2 pos)
{
    return texture(iChannel0,(fragCoord+pos)/iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    fragColor=texture(iChannel0, fragCoord/iResolution.xy); 

    if(fragColor.xyz == vec3(0.0) || mod(float(iFrame),runFor) == 0.0)
    {
        fragColor=texture(iChannel1, fragCoord/iResolution.xy); 
    }
    else
    {
		vec4 u = vec4(0.0);
    	float spillAmount = clamp(iTime*0.1,0.0,1.0);

        float samp1 = length(texture(iChannel0, fragCoord/iResolution.xy));
        float samp2 = length(texture(iChannel1, fragCoord/iResolution.xy));

        float ang1 = samp1*3.14*2.0;
        float x1 = cos(ang1);
        float y1 = sin(ang1);

        float ang2 = samp2*3.14*2.0;
        float x2 = cos(ang2);
        float y2 = sin(ang2);        

        u+=read(fragCoord,vec2(x1,y1)*spillAmount) * 0.05;
        u+=read(fragCoord,vec2(x2,y2)*spillAmount*vec2(-1.0,1.0)) * 0.95;
        //u/=2.0;
        fragColor = u;
    }
}
