void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 blackAndWhite = texture(iChannel0,uv);

    vec2 blackAndWhiteUV = vec2(0, blackAndWhite.x);

 	//vec2 blackAndWhiteUV = vec2(0.5 + 0.5*cos(iTime), blackAndWhite.x);

    float spinSpeed = 0.05;
    float cosMult 	=  cos(iTime * spinSpeed);
    float sinMult 	=  sin(iTime * spinSpeed);
    vec2 rotatedUVs = blackAndWhiteUV * mat2(cosMult, sinMult, -sinMult, cosMult);

    vec4 gradiantUsingUVs = texture(iChannel1,uv);
    //vec4 gradiantUsingCustomUVs = texture(iChannel1,blackAndWhiteUV);
    vec4 gradiantUsingCustomUVs = texture(iChannel1,rotatedUVs);


    fragColor = blackAndWhite;
    fragColor = vec4(blackAndWhite.x, blackAndWhite.x, blackAndWhite.x, 1.0);
    //fragColor = vec4(gradiantUsingUVs.xyz, 1.0);
    fragColor = vec4(gradiantUsingCustomUVs.xyz, 1.0);

}
