float ln (vec3 p, vec3 a, vec3 b) {return length(p-a-(b-a)*dot(p-a,b-a)/dot(b-a,b-a));}
void mainImage( out vec4 Q, in vec2 U )
{
    vec4 
        a = A(U),
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec3 no = normalize(vec3(e.z-w.z,n.z-s.z,.01));
    Q = vec4(a.w*vec3(.1,.5,.9),1);

    Q *= exp(-.2*ln(vec3(2,2,-1),vec3(U/R.y,0),vec3(U/R.y,0)+reflect(normalize(vec3((U-0.5*R)/R.y,1)),no)));
	//Q = a.wwww;
}

void mainImage( out vec4 Q, in vec2 U )
{
    #define N 3.
    for (float i = 0.; i < N; i++) {
        Q = A(U);
    	U -= Q.w*Q.xy/N;
    }
    Q = A(U);
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0)),
        m = 0.25*(n+e+s+w);
    Q.xy = Q.xy+Q.w*0.25*vec2(e.z-w.z,n.z-s.z);
    Q.xy -= G*(Q.w+MIN)/R.x/R.y;
    Q.z = Q.w*0.25*(n.z*n.y-s.z*s.y+e.z*e.x-w.z*w.x)+mix(Q.z,m.z,Q.w);
    Q.w = mix(Q.w,m.w,.02*Q.w)-Q.w*0.25*(n.z*n.w*n.y-s.z*s.w*s.y+e.z*e.w*e.x-w.z*w.w*w.x);
    Q.w = max(Q.w,0.);
    if (iMouse.z>0. && length(iMouse.xy-U)<10.) Q.z = 1.-.5*Q.w;
    if (length(U-vec2(0,.5)*R)<SOURCE) Q = mix(Q,vec4(JET,.9,MAX),vec4(.5,.1,.01,1));
    if (iFrame < 1) {
    	Q = vec4(0,0,1,MIN);
    }
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.) Q = vec4(0,0,1,MIN);
}

