float Circle(vec2 uv, vec2 p, float r, float blur, float o) {

    // Draw a circle
    float d = length(uv - p);
    float c = smoothstep(r, r-blur, d) * o;


    return c;

}

float Line(vec2 uv, vec2 p, float t, float s, float o) {

	// Draw a line
    float r = 0.1; // radius
    float b = sin(iTime*s + s + o)*.04 + .08; // blur
    float line = 0.;
    for (int i = 0; i < 5; i++)
        line += Circle(uv, vec2(
            float(i)*(sin(iTime*s + t + o)*0.006 + 0.012) + p.x,	// X location
            float(i)*(sin(iTime*s + t + o)*0.006 + 0.012) + p.y		// Y location
            ),
        r*(float(i)*.06 + .6), 
        b,
        sin(iTime*4. + t + o)*.2 + .6	// Opacity fluctuate
        );
    return line;

}

float Row(vec2 uv, float p, float t, float s, float o) {

	// Draw a bunch of lines
    float row = 0.;
    for (int i = 0; i < 6; i++)
        row += Line(uv, vec2(float(i)*.3-.76, p), float(i), 4., o);
    return row;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Adjust origin
    uv -= 0.5;

    // Adjust aspect ratio
    uv.x *= iResolution.x / iResolution.y;

    float cR = 0.;
    float cG = 0.;
    float cB = 0.;

    for (int i = 0; i < 3; i++)
		cR += Row(uv, float(i)*0.31 - .31, 0., 1., float(i));

    for (int i = 0; i < 3; i++)
		cG += Row(uv, float(i)*0.32 - .32, 0., 1., float(i-1));

    for (int i = 0; i < 3; i++)
		cB += Row(uv, float(i)*0.33 - .33, 0., 1., float(i-2));


    vec3 col = vec3(cR, cG, cB);    

    // Output to screen
    fragColor = vec4(col,1.0);
}
