//Click and drag

#define light normalize(vec3(float(iMouse.x)/iResolution.x - 0.5, float(iMouse.y)/iResolution.y - 0.5, 0.25))

float height(vec2 pos, float offx, float offy){
    vec3 tex = texture(iChannel0, pos-vec2(offx, offy)).rgb;
    return(pow(tex.r+tex.g+tex.b,2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 off = 0.5/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;
    vec3 albedo = texture(iChannel0, uv).rgb;
    vec3 ax = vec3(2.,0.,(height(uv,off.x,0.)-height(uv,0.,0.))+(height(uv,0.,0.)-height(uv,-off.x,0.)));
    vec3 bx = vec3(0.,2.,(height(uv,0.,off.y)-height(uv,0.,0.))+(height(uv,0.,0.)-height(uv,0.,-off.y)));
    vec3 normal = normalize(cross(ax, bx));
    float diff = dot(normal, light);
    vec3 hvec = normalize(normal+light);
    float spec = pow(dot(hvec,light),41.0);
    albedo = vec3(pow(albedo.x, 2.2), pow(albedo.y, 2.2), pow(albedo.z, 2.2));
    vec3 color = vec3(0.3*spec + 0.7*albedo*diff);
    color = vec3(pow(color.x,0.4545), pow(color.y,0.4545), pow(color.z, 0.4545));
    fragColor = vec4(color,1.0);
}
